var master = require('dice-master');

var _getNextPos = function (current, dice) {
    let next = master.board[current].next;
    if (dice == 1) {
        return next;
    }
    let nextList = [];
    for (let i = 0; i < next.length; i++) {
        let l = _getNextPos(next[i], dice - 1);
        nextList = nextList.concat(l);
    }
    return nextList;
}

module.exports = {
    getNextPos: function (current, dice) {
        return _getNextPos(current, dice);
    },
};